void clearScreen() {
   #ifdef _WIN32
       system("cls"); // Para Windows
   #else
       std::cout << "\033[2J\033[H"; // Para Linux/MacOS
   #endif
   }

void Escrever(const std::string texto, int vt) {
    
    for (char c : texto) {
        std::cout << c << std::flush;
        std::this_thread::sleep_for(std::chrono::milliseconds(50/vt));
    }

}

